  "FieldsHintTexts": {
    "ForApplicantInfoStep": [
      {
        "HintText": "Type to search BC OrgBook",
        "Field": "Telephone",
        "FieldName": "quartech_legalbusinessororganizationname"
      },
      {
        "HintText": "(___) ___-____",
        "Field": "Telephone",
        "FieldName": "quartech_telephone"
      }
    ],
    "ForProjectStep": [
      {
        "HintText": "",
        "Field": "A field on the Project step",
        "FieldName": "aField"
      }
    ],
    "ForEstimatedActivityBudgetStep": [
      {
        "HintText": "",
        "Field": "A field on the Estimated Activity Budget step",
        "FieldName": "aField"
      }
    ],
    "ForDemographicInfoStep": [
      {
        "HintText": "",
        "Field": "A field on the Demographic Info step",
        "FieldName": "aField"
      }
    ],
    "DeclarationAndConsentStep": [
      {
        "HintText": "",
        "Field": "A field on the Declaration And Consent step",
        "FieldName": "aField"
      }
    ]
  },
  "SystemNotice": "<br/><p>This is a new application system. Please bear with us as we work to improve the system. If you have any technical issues with the system or wish to provide feedback to help us to make it as user friendly as possible, please contact: <a href = 'mailto: PODS@gov.bc.ca'>PODS@gov.bc.ca</a></p>",
  "FieldsConfig": {
    "programs": [
      {
        "name": "ABPP1",
        "sections": [
          {
            "name": "ClaimInfoStep",
            "fieldsets": [
              {
                "name": "Application Information for Reimbursement",
                "fields": [
                  {
                    "name": "quartech_legalbusinessororganizationname",
                    "label": "Legal Business or Organization Name"
                  },
                  {
                    "name": "quartech_businessaddress",
                    "label": "Business Address"
                  },
                  {
                    "name": "quartech_businessphonenumber",
                    "label": "Tel"
                  },
                  {
                    "name": "quartech_businessemailaddress",
                    "label": "Email"
                  },
                  {
                    "name": "quartech_applicantinformationconfirmation",
                    "label": "I / We confirm the above information is correct",
                    "required": true,
                    "elementType": "DropdownSelect"
                  },
                  {
                    "name": "quartech_applicantinformationcorrections",
                    "label": "Please provide further detail",
                    "required": true,
                    "maxLength": 10000
                  },
                  {
                    "name": "quartech_filenumber",
                    "label": "File number (from authorization letter)",
                    "readOnly": true
                  },
                  {
                    "name": "quartech_projectenddate",
                    "label": "Project End Date",
                    "required": true,
                    "elementType": "DatePicker",
                    "tooltipText": "If the project is not yet completed, please provide an estimated project end date"
                  }
                ]
              },
              {
                "name": "Claim Information",
                "fields": [
                  {
                    "name": "quartech_authorizedclaimedamount",
                    "label": "Approved Amount as per Authorization Letter (Consultant Fee, GST, Total)",
                    "readOnly": true
                  },
                  {
                    "name": "quartech_totalfees",
                    "label": "Your requested amount in this current claim",
                    "format": "currency",
                    "required": true,
                    "validation": {
                      "type": "numeric",
                      "value": "0",
                      "comparison": "greaterThan"
                    },
                    "emptyInitialValue": true
                  }
                ]
              }
            ]
          },
          {
            "name": "ProjectIndicatorsStep",
            "fieldsets": [
              {
                "name": "Project Results",
                "fields": [
                  {
                    "name": "quartech_businessmanagementtrainingparticipants",
                    "label": "How many people participated in the training funded by this project, including the applicant?",
                    "required": true,
                    "format": "number",
                    "validation": {
                      "type": "numeric",
                      "value": "0",
                      "comparison": "greaterThan"
                    }
                  },
                  {
                    "name": "quartech_adoptedprojectresults",
                    "label": "Did you adopt any technologies, equipment, practices or processes as a result of this project?",
                    "required": true,
                    "elementType": "DropdownSelect"
                  },
                  {
                    "name": "quartech_adoptednumber",
                    "label": "How many?",
                    "required": true,
                    "format": "number",
                    "validation": {
                      "type": "numeric",
                      "value": "0",
                      "comparison": "greaterThan"
                    }
                  },
                  {
                    "name": "quartech_environmentallybeneficialadoptedresults",
                    "label": "Did you adopt any technologies, processes or equipment that are environmentally beneficial as a result of this project?",
                    "required": true,
                    "elementType": "DropdownSelect"
                  },
                  {
                    "name": "quartech_environmentallybeneficialadoptednumber",
                    "label": "How many?",
                    "required": true,
                    "format": "number",
                    "validation": {
                      "type": "numeric",
                      "value": "0",
                      "comparison": "greaterThan"
                    }
                  }
                ]
              }
            ]
          },
          {
            "name": "DocumentsStep",
            "fieldsets": [
              {
                "name": "Supporting Documents",
                "fields": [
                  {
                    "name": "quartech_invoices",
                    "label": "Invoice(s) from Learning Provider(s) (as per approved learning action plan)",
                    "required": true,
                    "elementType": "FileInput"
                  },
                  {
                    "name": "quartech_proofofpayment",
                    "label": "Proof of payment",
                    "required": true,
                    "elementType": "FileInput"
                  }
                ]
              }
            ]
          }
        ]
      }
    ]
  }
}

quartech_applicantportalapplicationformc:
{
    "name": "ABPP1 - B.C. Agri-Business Planning Program",
    "hiddenSteps": "",
    "sections": [
        {
            "name": "ApplicantInfoStep",
            "fields": [
                {
                    "name": "quartech_indigenousapplicant",
                    "required": true,
                    "elementType": "DropdownSelect"
                },
                {
                    "name": "quartech_recipienttype",
                    "required": true,
                    "elementType": "DropdownSelect",
                    "label": "Type of Business / Organization. If your operation does both processing and primary production, please select the one that will primarily benefit from this program."
                },
                {
                    "name": "quartech_commodity",
                    "visibleIf": {
                        "fieldName": "quartech_recipienttype",
                        "selectedValue": 255550001
                    }
                },
                {
                    "name": "quartech_othercommoditiesproducedharvested",
                    "visibleIf": {
                        "fieldName": "quartech_recipienttype",
                        "selectedValue": 255550001
                    }
                },
                {
                    "name": "quartech_primarilyprocess",
                    "visibleIf": {
                        "fieldName": "quartech_recipienttype",
                        "selectedValue": 255550002
                    }
                },
                {
                    "name": "quartech_otherproductsprocessed",
                    "visibleIf": {
                        "fieldName": "quartech_recipienttype",
                        "selectedValue": 255550002
                    }
                },
                {
                    "name": "quartech_operationdoesprocessingandprimaryproducti",
                    "required": true,
                    "elementType": "DropdownSelect"
                },
                {
                    "name": "quartech_applicanttype",
                    "required": true,
                    "elementType": "DropdownSelect"
                },
                {
                    "name": "quartech_yearproductionestablished",
                    "required": true,
                    "validation": {
                        "type": "numeric",
                        "value": "1900",
                        "comparison": "greaterThan"
                    }
                },
                {
                    "name": "quartech_numberoffulltimeemployees",
                    "required": true,
                    "validation": {
                        "type": "numeric",
                        "value": "0",
                        "comparison": "greaterThanOrEqualTo"
                    }
                },
                {
                    "name": "quartech_numberofparttimeemployees",
                    "required": true,
                    "validation": {
                        "type": "numeric",
                        "value": "0",
                        "comparison": "greaterThanOrEqualTo"
                    }
                },
                {
                    "name": "quartech_previousyeargrossincome",
                    "required": true,
                    "format": "currency",
                    "maxDigits": 13,
                    "emptyInitialValue": true,
                    "validation": {
                        "type": "numeric",
                        "value": "0",
                        "comparison": "greaterThanOrEqualTo"
                    }
                },
                {
                    "name": "quartech_previousyearnetincome",
                    "required": true,
                    "format": "currency",
                    "maxDigits": 13,
                    "emptyInitialValue": true,
                    "allowNegatives": true
                },
                {
                    "name": "quartech_operationdoesprocessingandprimaryproducti",
                    "required": true,
                    "elementType": "DropdownSelect"
                },
                {
                    "name": "quartech_agriprogramsubscriber",
                    "elementType": "DropdownSelect",
                    "required": true
                },
                {
                    "name": "quartech_tipreportenrolled",
                    "visibleIf": {
                        "fieldName": "quartech_agriprogramsubscriber",
                        "selectedValue": 255550001
                    }
                },
                {
                    "name": "quartech_businessdescription",
                    "required": true
                }
            ]
        },
        {
            "name": "EligibilityStep",
            "fields": [
                {
                    "name": "quartech_bcregisteredbusinessentity",
                    "required": true,
                    "elementType": "DropdownSelect"
                },
                {
                    "name": "quartech_farmbusinessistaxableentity",
                    "required": true,
                    "elementType": "DropdownSelect"
                },
                {
                    "name": "quartech_allproprietersarebcresidents",
                    "required": true,
                    "elementType": "DropdownSelect"
                },
                {
                    "name": "quartech_annualgrossrevenueoverlimit",
                    "elementType": "DropdownSelect",
                    "required": true,
                    "label": "Did you report total annual gross revenues greater than $50,000/year in the previous year to Canada Revenue Agency? - This eligibility requirement may not be applicable to Indigenous applicants."
                },
                {
                    "name": "quartech_abppsupportoverlimit",
                    "required": true,
                    "elementType": "DropdownSelect"
                },
                {
                    "name": "quartech_abppsupportoverlimitdetail",
                    "visibleIf": {
                        "fieldName": "quartech_abppsupportoverlimit",
                        "selectedValue": 255550000,
                        "techComment": "Yes value"
                    }
                },
                {
                    "name": "quartech_supportreceivedduringscap",
                    "required": true,
                    "elementType": "DropdownSelect"
                },
                {
                    "name": "quartech_supportduringscapdetails",
                    "visibleIf": {
                        "fieldName": "quartech_supportreceivedduringscap",
                        "selectedValue": 255550000,
                        "techComment": "Yes value"
                    }
                }
            ]
        },
        {
            "name": "ProjectStep",
            "fields": [
                {
                    "name": "quartech_applicantsprojectname",
                    "required": true,
                    "label": "Event/Training Title",
                    "tooltipText": "The name of the event/training"
                },
                {
                    "name": "quartech_projectdescription",
                    "required": true,
                    "label": "Event/Training Description"
                },
                {
                    "name": "quartech_whenistheprojectstartdate",
                    "required": true,
                    "elementType": "DatePicker"
                },
                {
                    "name": "quartech_activityenddate",
                    "required": true,
                    "elementType": "DatePicker"
                },
                {
                    "name": "quartech_ifotherpleasedescribe",
                    "visibleIf": {
                        "fieldName": "quartech_inordertocontinuouslyimprovecommunications",
                        "selectedValue": 255550006,
                        "techComment": "Other value"
                    }
                },
                {
                    "name": "Consultant Information",
                    "type": "SectionTitle",
                    "hidden": true
                },
                {
                    "name": "Business Plan",
                    "type": "SectionTitle",
                    "hidden": true
                }
            ]
        },
        {
            "name": "DeliverablesBudgetStep",
            "fields": [
                {
                    "name": "quartech_estimatedbudgettotalactivitycost",
                    "format": "currency",
                    "maxDigits": 11,
                    "required": true,
                    "label": "Total proposed budget",
                    "validation": {
                        "type": "numeric",
                        "value": "0",
                        "comparison": "greaterThanOrEqualTo"
                    }
                },
                {
                    "name": "quartech_detailedproposedbudget",
                    "required": true,
                    "maxLength": 10000,
                    "label": "You can either upload an event/training budget document as an attachment or explain the proposed budget in the following text box. If you are uploading the budget as an attachment (in the Documents tab), please enter the file name in this text box (1000 words max)."
                },
                {
                    "name": "quartech_deliverables",
                    "required": true,
                    "maxLength": 10000,
                    "label": "Please describe the expected learnings or takeaways you have of the experience and how this will improve your business."
                }
            ]
        }
    ]
}

